import React, { Component } from 'react';
import { connect } from 'react-redux';
import propTypes from 'prop-types';
import { fetchCurrencies, fetchCurrenciesFull } from '../redux/actions';
import '../style/components/WalletForm.css';

class WalletForm extends Component {
  constructor() {
    super();

    this.state = {
      id: 0,
      value: 0,
      description: '',
      currency: 'USD',
      method: 'money',
      tag: 'food',
      total: '',
      exchangeRates: {},
    };
  }

  componentDidMount() {
    const { dispatch } = this.props;
    dispatch(fetchCurrencies());
  }

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const { dispatch /* , exchangeRates  */ } = this.props;
    const { id /* , currency, value, total */ } = this.state;
    /* const partial = total; */
    let idCount = id;
    /* const expenseCurrency = currency; */
    this.setState({
      id: idCount += 1,
      /* total: Number(partial) + (Number(value) * Number(exchangeRates[expenseCurrency].ask))
        .toFixed(2), */
    });
    dispatch(fetchCurrenciesFull(this.state));
    this.setState({
      value: '',
      description: '',
      currency: 'USD',
      method: 'money',
      tag: 'food',
    });
  };

  render() {
    const { currencies } = this.props;
    const { value, description, currency, method, tag } = this.state;
    return (
      <form
        className="wallet-form-box"
        onSubmit={ this.handleSubmit }
      >
        <label htmlFor="value">
          Valor:
          {' '}
          <input
            data-testid="value-input"
            name="value"
            type="number"
            onChange={ this.handleChange }
            value={ value }
          />
        </label>

        <label htmlFor="description">
          Descrição:
          {' '}
          <input
            data-testid="description-input"
            name="description"
            type="text"
            onChange={ this.handleChange }
            value={ description }
          />
        </label>

        <label htmlFor="currency">
          Moeda:
          {' '}
          <select
            data-testid="currency-input"
            name="currency"
            onChange={ this.handleChange }
            value={ currency }
          >
            <option value={ currencies[0] }>{ currencies[0] }</option>
            <option value={ currencies[1] }>{ currencies[1] }</option>
            <option value={ currencies[2] }>{ currencies[2] }</option>
            <option value={ currencies[3] }>{ currencies[3] }</option>
            <option value={ currencies[4] }>{ currencies[4] }</option>
            <option value={ currencies[5] }>{ currencies[5] }</option>
            <option value={ currencies[6] }>{ currencies[6] }</option>
            <option value={ currencies[7] }>{ currencies[7] }</option>
            <option value={ currencies[8] }>{ currencies[8] }</option>
            <option value={ currencies[9] }>{ currencies[9] }</option>
            <option value={ currencies[10] }>{ currencies[10] }</option>
            <option value={ currencies[11] }>{ currencies[11] }</option>
            <option value={ currencies[12] }>{ currencies[12] }</option>
            <option value={ currencies[13] }>{ currencies[13] }</option>
            <option value={ currencies[14] }>{ currencies[14] }</option>
          </select>
        </label>

        <label htmlFor="method">
          Método de Pagamento:
          {' '}
          <select
            data-testid="method-input"
            name="method"
            onChange={ this.handleChange }
            value={ method }
          >
            <option value="money">Dinheiro</option>
            <option value="credit-card">Cartão de crédito</option>
            <option value="debit-card">Cartão de débito</option>
          </select>
        </label>

        <label htmlFor="tag">
          Categoria:
          {' '}
          <select
            data-testid="tag-input"
            name="tag"
            onChange={ this.handleChange }
            value={ tag }
          >
            <option value="food">Alimentação</option>
            <option value="leisure">Lazer</option>
            <option value="work">Trabalho</option>
            <option value="transport">Transporte</option>
            <option value="health">Saúde</option>
          </select>
        </label>

        <button
          type="submit"
        >
          Adicionar Despesa
        </button>

      </form>
    );
  }
}

const mapStateToProps = (state) => ({
  currencies: state.wallet.currencies,
  currency: state.wallet.expenses[0].currency,
  exchangeRates: state.wallet.expenses[0].exchangeRates,
});

WalletForm.propTypes = {
  dispatch: propTypes.func.isRequired,
  currencies: propTypes.arrayOf(propTypes.string).isRequired,
  exchangeRates: propTypes.objectOf(propTypes.string).isRequired,
};

export default connect(mapStateToProps)(WalletForm);
