import { REQUEST_CURRENCY,
  RECEIVE_CURRENCY,
  RECEIVE_CURRENCY_FAILURE,
  RECEIVE_EXPENSE,
  RECEIVE_CURRENCY_FULL,
} from '../actions';

const INITIAL_STATE = {
  currencies: [],
  currenciesFull: {},
  expenses: [],
  error: '',
};

function wallet(state = INITIAL_STATE, action) {
  switch (action.type) {
  case REQUEST_CURRENCY: return { ...state, ...action.payload };
  case RECEIVE_CURRENCY: return { ...state, currencies: action.currencies };
  case RECEIVE_CURRENCY_FULL: return { ...state, expenses: action.currenciesFull };
  case RECEIVE_CURRENCY_FAILURE: return { ...state, error: action.error };
  case RECEIVE_EXPENSE: return { ...state, expenses: action.expenses };
  default: return state;
  }
}

export default wallet;
