import { REQUEST_CURRENCY } from '../actions';

const INITIAL_STATE = {
  currencies: [],
};

function currencyReducer(state = INITIAL_STATE, action) {
  switch (action.type) {
  case REQUEST_CURRENCY: return { ...state, ...action.payload };
  default: return state;
  }
}

export default currencyReducer;
