import { ADD_LOGIN_INFO } from '../actions';

const INITIAL_STATE = {
  email: '',
};

function user(state = INITIAL_STATE, action) {
  switch (action.type) {
  case ADD_LOGIN_INFO: return { ...action.payload.email.toString() };
  default: return state;
  }
}

export default user;
