import { OPEN_EDIT_FORM } from '../actions';

const INITIAL_STATE = {
  value: '',
  description: '',
  currency: '',
  method: '',
  tag: '',
  buttonText: 'Adicionar Despesa',
};

function user(state = INITIAL_STATE, action) {
  switch (action.type) {
  case OPEN_EDIT_FORM: return { ...state, email: action.email };
  default: return state;
  }
}

export default user;
