import { REQUEST_CURRENCY, RECEIVE_CURRENCY, RECEIVE_CURRENCY_FAILURE } from '../actions';

const INITIAL_STATE = {
  currencies: [],
  error: '',
};

function currencyReducer(state = INITIAL_STATE, action) {
  switch (action.type) {
  case REQUEST_CURRENCY: return { ...state, ...action.payload };
  case RECEIVE_CURRENCY: return { ...state, currencies: action.currencies };
  case RECEIVE_CURRENCY_FAILURE: return { ...state, error: action.error };
  default: return state;
  }
}

export default currencyReducer;
