import { ADD_LOGIN_INFO } from '../actions';

const user = {
  email: '',
  password: '',
};

function loginReducer(state = user, action) {
  switch (action.type) {
  case ADD_LOGIN_INFO: return { ...state, ...action.payload };
  default: return state;
  }
}

export default loginReducer;
